1.4 Preppin

calc_year &lt;- \(date, ref = ymd("2014-09-01")){
  ceiling((interval(date, ref) / years(1)) + 1)
}


output &lt;- raw |&gt;
  mutate(pc = if_else(
    `Parental Contact` == 1,
    `Parental Contact Name_1`,
    `Parental Contact Name_2`
    )
    ) |&gt; 
  transmute(
    "Academic Year" = calc_year(`Date of Birth`),
    "Pupil's Name" = paste0(`pupil last name`, ", ", `pupil first name`),
    "Parental Contact Full Name" = paste0(`pupil last name`, ", ", pc),
    "Parental Contact Email Address" = paste0(pc, ".", `pupil last name`, "@", `Preferred Contact Employer`, ".com")
    )

3.4 Preppin

output &lt;- raw_1 |&gt;
  select(`Student ID` = id, Gender = gender) |&gt;
  left_join(
    raw_2 |&gt;
      pivot_longer(-`Student ID`, names_to = "Subject", values_to = "Score") |&gt;
      summarize(
        `Passed Subjects` = sum(Score &gt;= 75),
        `Student's Avg Score` = round(mean(Score), 1),
        .by = `Student ID`
      ),
    by = "Student ID"
  ) |&gt;
  select(`Passed Subjects`, `Student's Avg Score`, `Student ID`, Gender)

4.4 Preppin

output &lt;- raw_2 |&gt;
  pivot_longer(-`Student ID`,
               names_to = "Weekday",
               values_to = "Method of Travel") |&gt;
  mutate(
    `Method of Travel` = case_match(
      `Method of Travel`,
      "Bycycle" ~ "Bicycle",
      "Carr" ~ "Car",
      c("Scootr", "Scoter") ~ "Scooter",
      c("Walkk", "Wallk", "WAlk", "Waalk") ~ "Walk",
      "Helicopeter" ~ "Helicopter",
      .default = `Method of Travel`
    ),
    `Sustainable?` = case_match(
      `Method of Travel`,
      c("Bicycle", "Dad's Shoulders", "Hopped", "Jumped", 
        "Mum's Shoulders", "Scooter", "Skipped", "Walk") ~ "Sustainable",
      .default = "Non-Sustainable"
      )
  ) |&gt; 
  count(`Sustainable?`, `Method of Travel`, Weekday, name = "Number of Trips") |&gt; 
  add_count(Weekday, wt = `Number of Trips`, name = "Trips per Day") |&gt; 
  mutate(`% of trips per day` = round(`Number of Trips` / `Trips per Day`, 2))

5.4 Preppin

mid &lt;- raw |&gt; 
  pivot_longer(-`Student ID`, names_to = "Subject", values_to = "Score") |&gt; 
  mutate(Grade = LETTERS[7 - ntile(Score, 6)], .by = "Subject") |&gt; 
  mutate(
    Points = case_match(
      Grade,
      "A" ~ 10,
      "B" ~ 8,
      "C" ~ 6,
      "D" ~ 4,
      "E" ~ 2,
      "F" ~ 1
    )
    ) |&gt; 
  mutate(`Total points per Student` = sum(Points), .by = `Student ID`) |&gt; 
  mutate(`Avg student total points per grade` = 
           round(mean(`Total points per Student`) ,2),
         .by = Grade)

avg_a &lt;- mid |&gt; 
  filter(Grade == "A") |&gt; 
  slice(1) |&gt; 
  pull(`Avg student total points per grade`)

output &lt;- mid |&gt; 
  filter(`Total points per Student` &gt;= avg_a, Grade != "A")

6.4 Preppin

mid_2 &lt;- raw_2 |&gt;
  extract(Scrabble, c("Points", "other"), "(^[0-9]+) points?:(.*)", convert = T) |&gt; 
  mutate(other = str_squish(other)) |&gt; 
  separate_rows(other, sep = ", ") |&gt; 
  separate(other, into = c("Tiles", "Frequency"), sep = " ×", convert = T) |&gt; 
  mutate(Chance = round(Frequency / sum(Frequency), 2))

output &lt;- raw_1 |&gt; 
  mutate(letters = str_split(`7 letter word`, pattern = "")) |&gt; 
  unnest(letters) |&gt; mutate(letters = str_to_upper(letters)) |&gt; 
  count(`7 letter word`, letters) |&gt; 
  left_join(mid_2, by = c("letters" = "Tiles")) |&gt; 
  mutate(`Updated Chance` = if_else(n &lt;= Frequency, Chance ^ n, 0)) |&gt; 
  summarize(
    `% Chance` = prod(`Updated Chance`),
    `Total Points` = sum(Points * n),
    .by = `7 letter word`
  ) |&gt; 
  filter(`% Chance` &gt; 0) |&gt; 
  mutate(
    `Points Rank` = dense_rank(desc(`Total Points`)),
    `Likelihood Rank` = dense_rank(desc(`% Chance`))
    ) |&gt; 
  select(`Points Rank`, `Likelihood Rank`, `7 letter word`, 
         `% Chance`, `Total Points`) |&gt; 
  arrange(`Points Rank`)

8.4 Preppin

output &lt;- evolutions |&gt; 
  filter(!is.na(Stage_2)) |&gt; 
  mutate(final_evol = coalesce(Stage_3, Stage_2)) |&gt; 
  left_join(
    stats |&gt; 
      transmute(
        name, pokedex_number, gen_introduced,
        initial_combat_power = rowSums(across(hp:speed))),
    by = c("Stage_1" = "name")
  ) |&gt; 
  left_join(
    stats |&gt; 
      transmute(name, final_combat_power = rowSums(across(hp:speed))),
    by = c("final_evol" = "name")
  ) |&gt; 
  mutate(combat_power_increase = (final_combat_power - initial_combat_power) / initial_combat_power) |&gt; 
  arrange(combat_power_increase) |&gt; 
  select(-final_evol)
